CREATE PROCEDURE ContadoresTablasSeq
AS
BEGIN

	declare @tablaaux nvarchar(max);
	declare @numreg nvarchar(50);
	declare @sqlCommand nvarchar(max);
	declare @sqlparam nvarchar(max);
	declare @currentseq nvarchar(50);
	declare @secuencia nvarchar(100);

	declare cursortablas cursor
	for select a.name from sys.tables a
    order by a.name;

	drop table tempcontadores;
	CREATE TABLE  tempcontadores
	(
	tabla nvarchar(max),
	numreg int,
	secuencia nvarchar(max),
	valoractual int
	);

BEGIN TRY
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;
		

	open cursortablas;
	fetch next from  cursortablas
	into @tablaaux;

	--print 'inicio '; 
	
	WHILE @@FETCH_STATUS = 0
	BEGIN


		set @sqlCommand = 'select @numregout = count(*) from ' + @tablaaux + ';'  ;
		print @sqlcommand;

		SET @sqlParam = N'@numregout int OUTPUT'; 

		EXECUTE sp_executesql @sqlCommand, @sqlparam, 
							  @numregout = @numreg OUTPUT ; 
		--SELECT @numreg;

		--print 'numregistros ' +  @numreg;

		set @secuencia = null;
		set @currentseq = null;

		select @secuencia = b.name, @currentseq = CAST(b.current_value AS INT)
		from  sys.sequences b 
		where SUBSTRING(b.name, 0, charindex('seq',b.name, 1)) = @tablaaux;
		

		--print 'currentvalue ' +  @currentseq;

		insert into tempcontadores
		(tabla , numreg, secuencia, valoractual)
		select @tablaaux, @numreg , @secuencia, @currentseq      ;

		fetch next from  cursortablas
		into @tablaaux;
	END;

	--print 'fin '; 

	
	close cursortablas;
	DEALLOCATE cursortablas;

END try 
BEGIN CATCH
	print 'ERROR '; 
	close cursortablas;
	DEALLOCATE cursortablas;
END CATCH;

END;

	CREATE PROCEDURE dbo.sp_alterdiagram
	(
		@diagramname 	sysname,
		@owner_id	int	= null,
		@version 	int,
		@definition 	varbinary(max)
	)
	WITH EXECUTE AS 'dbo'
	AS
	BEGIN
		set nocount on
	
		declare @theId 			int
		declare @retval 		int
		declare @IsDbo 			int
		
		declare @UIDFound 		int
		declare @DiagId			int
		declare @ShouldChangeUID	int
	
		if(@diagramname is null)
		begin
			RAISERROR ('Invalid ARG', 16, 1)
			return -1
		end
	
		execute as caller;
		select @theId = DATABASE_PRINCIPAL_ID();	 
		select @IsDbo = IS_MEMBER(N'db_owner'); 
		if(@owner_id is null)
			select @owner_id = @theId;
		revert;
	
		select @ShouldChangeUID = 0
		select @DiagId = diagram_id, @UIDFound = principal_id from dbo.sysdiagrams where principal_id = @owner_id and name = @diagramname 
		
		if(@DiagId IS NULL or (@IsDbo = 0 and @theId <> @UIDFound))
		begin
			RAISERROR ('Diagram does not exist or you do not have permission.', 16, 1);
			return -3
		end
	
		if(@IsDbo <> 0)
		begin
			if(@UIDFound is null or USER_NAME(@UIDFound) is null) -- invalid principal_id
			begin
				select @ShouldChangeUID = 1 ;
			end
		end

		-- update dds data			
		update dbo.sysdiagrams set definition = @definition where diagram_id = @DiagId ;

		-- change owner
		if(@ShouldChangeUID = 1)
			update dbo.sysdiagrams set principal_id = @theId where diagram_id = @DiagId ;

		-- update dds version
		if(@version is not null)
			update dbo.sysdiagrams set version = @version where diagram_id = @DiagId ;

		return 0
	END

CREATE PROCEDURE dbo.sp_creatediagram
(
	@diagramname 	sysname,
	@owner_id		int	= null, 	
	@version 		int,
	@definition 	varbinary(max)
)
WITH EXECUTE AS 'dbo'
AS
BEGIN
	set nocount on

	declare @theId int
	declare @retval int
	declare @IsDbo	int
	declare @userName sysname
	if(@version is null or @diagramname is null)
	begin
		RAISERROR (N'E_INVALIDARG', 16, 1);
		return -1
	end

	execute as caller;
	select @theId = DATABASE_PRINCIPAL_ID(); 
	select @IsDbo = IS_MEMBER(N'db_owner');
	revert; 
	
	if @owner_id is null
	begin
		select @owner_id = @theId;
	end
	else
	begin
		if @theId <> @owner_id
		begin
			if @IsDbo = 0
			begin
				RAISERROR (N'E_INVALIDARG', 16, 1);
				return -1
			end
			select @theId = @owner_id
		end
	end
	-- next 2 line only for test, will be removed after define name unique
	if EXISTS(select diagram_id from dbo.sysdiagrams where principal_id = @theId and name = @diagramname)
	begin
		RAISERROR ('The name is already used.', 16, 1);
		return -2
	end

	insert into dbo.sysdiagrams(name, principal_id , version, definition)
			VALUES(@diagramname, @theId, @version, @definition) ;
	
	select @retval = @@IDENTITY 
	return @retval
END

CREATE PROCEDURE dbo.sp_dropdiagram
(
	@diagramname 	sysname,
	@owner_id	int	= null
)
WITH EXECUTE AS 'dbo'
AS
BEGIN
	set nocount on
	declare @theId 			int
	declare @IsDbo 			int
	
	declare @UIDFound 		int
	declare @DiagId			int

	if(@diagramname is null)
	begin
		RAISERROR ('Invalid value', 16, 1);
		return -1
	end

	EXECUTE AS CALLER;
	select @theId = DATABASE_PRINCIPAL_ID();
	select @IsDbo = IS_MEMBER(N'db_owner'); 
	if(@owner_id is null)
		select @owner_id = @theId;
	REVERT; 
	
	select @DiagId = diagram_id, @UIDFound = principal_id from dbo.sysdiagrams where principal_id = @owner_id and name = @diagramname 
	if(@DiagId IS NULL or (@IsDbo = 0 and @UIDFound <> @theId))
	begin
		RAISERROR ('Diagram does not exist or you do not have permission.', 16, 1)
		return -3
	end

	delete from dbo.sysdiagrams where diagram_id = @DiagId;

	return 0;
END

	CREATE PROCEDURE dbo.sp_helpdiagramdefinition
	(
		@diagramname 	sysname,
		@owner_id	int	= null 		
	)
	WITH EXECUTE AS N'dbo'
	AS
	BEGIN
		set nocount on

		declare @theId 		int
		declare @IsDbo 		int
		declare @DiagId		int
		declare @UIDFound	int
	
		if(@diagramname is null)
		begin
			RAISERROR (N'E_INVALIDARG', 16, 1);
			return -1
		end
	
		execute as caller;
		select @theId = DATABASE_PRINCIPAL_ID();
		select @IsDbo = IS_MEMBER(N'db_owner');
		if(@owner_id is null)
			select @owner_id = @theId;
		revert; 
	
		select @DiagId = diagram_id, @UIDFound = principal_id from dbo.sysdiagrams where principal_id = @owner_id and name = @diagramname;
		if(@DiagId IS NULL or (@IsDbo = 0 and @UIDFound <> @theId ))
		begin
			RAISERROR ('Diagram does not exist or you do not have permission.', 16, 1);
			return -3
		end

		select version, definition FROM dbo.sysdiagrams where diagram_id = @DiagId ; 
		return 0
	END

CREATE PROCEDURE dbo.sp_renamediagram
(
	@diagramname 		sysname,
	@owner_id		int	= null,
	@new_diagramname	sysname

)
WITH EXECUTE AS 'dbo'
AS
BEGIN
	set nocount on
	declare @theId 			int
	declare @IsDbo 			int
	
	declare @UIDFound 		int
	declare @DiagId			int
	declare @DiagIdTarg		int
	declare @u_name			sysname
	if((@diagramname is null) or (@new_diagramname is null))
	begin
		RAISERROR ('Invalid value', 16, 1);
		return -1
	end

	EXECUTE AS CALLER;
	select @theId = DATABASE_PRINCIPAL_ID();
	select @IsDbo = IS_MEMBER(N'db_owner'); 
	if(@owner_id is null)
		select @owner_id = @theId;
	REVERT;

	select @u_name = USER_NAME(@owner_id)

	select @DiagId = diagram_id, @UIDFound = principal_id from dbo.sysdiagrams where principal_id = @owner_id and name = @diagramname 
	if(@DiagId IS NULL or (@IsDbo = 0 and @UIDFound <> @theId))
	begin
		RAISERROR ('Diagram does not exist or you do not have permission.', 16, 1)
		return -3
	end

	-- if((@u_name is not null) and (@new_diagramname = @diagramname))	-- nothing will change
	--	return 0;

	if(@u_name is null)
		select @DiagIdTarg = diagram_id from dbo.sysdiagrams where principal_id = @theId and name = @new_diagramname
	else
		select @DiagIdTarg = diagram_id from dbo.sysdiagrams where principal_id = @owner_id and name = @new_diagramname

	if((@DiagIdTarg is not null) and  @DiagId <> @DiagIdTarg)
	begin
		RAISERROR ('The name is already used.', 16, 1);
		return -2
	end		

	if(@u_name is null)
		update dbo.sysdiagrams set [name] = @new_diagramname, principal_id = @theId where diagram_id = @DiagId
	else
		update dbo.sysdiagrams set [name] = @new_diagramname where diagram_id = @DiagId
	return 0
END

CREATE PROCEDURE dbo.sp_upgraddiagrams AS BEGIN IF OBJECT_ID(N'dbo.sysdiagrams') IS NOT NULL return 0;
CREATE TABLE dbo.sysdiagrams ( name sysname NOT NULL, principal_id int NOT NULL, -- we may change it to varbinary(85) diagram_id int PRIMARY KEY IDENTITY, version int,
definition varbinary(max) CONSTRAINT UK_principal_name UNIQUE ( principal_id, name ) );
/* Add this if we need to have some form of extended properties for diagrams */ /* IF OBJECT_ID(N'dbo.sysdiagram_properties') IS NULL BEGIN CREATE TABLE dbo.sysdiagram_properties ( diagram_id int, name sysname, value varbinary(max) NOT NULL ) END */
IF OBJECT_ID(N'dbo.dtproperties') IS NOT NULL begin insert into dbo.sysdiagrams ( [name], [principal_id], [version], [definition] ) select convert(sysname, dgnm.[uvalue]), DATABASE_PRINCIPAL_ID(N'dbo'), -- will change to the sid of sa 0, -- zero for old format, dgdef.[version], dgdef.[lvalue] from dbo.[dtproperties] dgnm inner join dbo.[dtproperties] dggd on dggd.[property] = 'DtgSchemaGUID' and dggd.[objectid] = dgnm.[objectid] inner join dbo.[dtproperties] dgdef on dgdef.[property] = 'DtgSchemaDATA' and dgdef.[objectid] = dgnm.[objectid]
where dgnm.[property] = 'DtgSchemaNAME' and dggd.[uvalue] like N'_EA3E6268-D998-11CE-9454-00AA00A3F36E_' return 2; end return 1; END
